mapper.select:
	SELECT obj.* FROM redpigmall_goods obj 


		JOIN redpigmall_store s ON s.id = obj.goods_store_id
		JOIN redpigmall_user u ON s.id = u.store_id
		AND u.userName = #{goods_store_user_userName,jdbcType=VARCHAR} 



		JOIN redpigmall_goods_ugc ugc ON obj.id = ugc.goods_id and ugc.class_id=#{ugc_id,jdbcType=BIGINT}



		JOIN redpigmall_store s ON obj.goods_store_id = s.id
		JOIN redpigmall_user u ON u.store_id = s.id



		JOIN redpigmall_goods_ugc gu ON obj.id = gu.goods_id AND

           gu.class_id=#{item,jdbcType=BIGINT}




		JOIN redpigmall_goods_spec gs on obj.id = gs.goods_id
		JOIN redpigmall_goodsspecproperty gsp on gsp.id = gs.spec_id and

            gs.spec_id = #{item,jdbcType=BIGINT}



	where 1=1 




				order by ${orderBy} ${orderType}


				order by obj.addTime desc



			limit #{currentPage},#{pageSize}

	SELECT obj.* FROM  redpigmall_goods obj

		JOIN redpigmall_store store ON (obj.goods_store_id = store.id and store.store_name LIKE concat(concat('%',#{redPig_store_name}),'%'))


		JOIN redpigmall_goodsclass gc ON gc.id = obj.gc_id
		JOIN redpigmall_goodsclass gc_parent ON gc.parent_id = gc_parent.id
		AND (
			obj.gc_id = #{rgc_id,jdbcType=BIGINT} 
			OR gc.parent_id = #{rgc_parent_id,jdbcType=BIGINT} 
			OR gc_parent.parent_id = #{rgc_parent_parent_id,jdbcType=BIGINT} 
		)



		JOIN redpigmall_goods_ugc ugc ON obj.id = ugc.goods_id and ugc.class_id=#{ugc_id,jdbcType=BIGINT}



		JOIN redpigmall_store s ON s.id = obj.goods_store_id
		JOIN redpigmall_user u ON s.id = u.store_id
		AND u.userName = #{goods_store_user_userName,jdbcType=VARCHAR} 


		JOIN redpigmall_store s ON obj.goods_store_id = s.id
		JOIN redpigmall_user u ON u.store_id = s.id and u.id =  #{goods_store_user_id,jdbcType=BIGINT} 


		JOIN redpigmall_goods_ugc gu ON obj.id = gu.goods_id AND

           gu.class_id=#{item,jdbcType=BIGINT}



		JOIN redpigmall_goods_spec gs on obj.id = gs.goods_id
		JOIN redpigmall_goodsspecproperty gsp on gsp.id = gs.spec_id and

            gs.spec_id = #{item,jdbcType=BIGINT}




		1=1



				ORDER BY ${orderBy} ${orderType}


				ORDER BY obj.addTime desc




		LIMIT #{currentPage},#{pageSize}

