select goods.id, goods.goods_collect, goods.goods_current_price,goods.goods_name,goods.goods_salenum,goods.goods_type,
goods.bad_evaluate, acc.path,acc.name

from redpigmall_goods as goods, redpigmall_accessory as acc
where 1 = 1

and goods.gc_id = #{gc_id}


and goods.deleteStatus = #{deleteStatus}


and goods.goods_status = #{goods_status}


and goods.goods_brand_id = #{goods_brand_id}

and goods.goods_main_photo_id = acc.id

order by goods.${orderBy} ${orderType}


limit #{start}, #{count}
