mapper.select:
	SELECT obj.* FROM redpigmall_store obj

		join redpigmall_store store on store.id = obj.store_id
		join redpigmall_storegrade sg on (sg.id = store.grade_id
		and sg.id in 

            #{item}

        )


		JOIN redpigmall_storegrade sg ON  (obj.grade_id = sg.id and sg.id=#{redpig_grade_id,jdbcType=BIGINT})


		JOIN redpigmall_user u ON  (u.store_id = obj.id and u.id=#{user_id,jdbcType=BIGINT})


		JOIN redpigmall_area a ON s.area_id = a.id 
		JOIN redpigmall_area p ON a.parent_id = p.id 
		JOIN redpigmall_area pp ON p.parent_id = pp.id and pp.id = #{area_parent_parent_id,jdbcType=BIGINT}


		JOIN redpigmall_area a ON s.area_id = a.id 
		JOIN redpigmall_area p ON a.parent_id = p.id and a.parent_id = #{area_paren_id,jdbcType=BIGINT}

	where 1=1 



				order by ${orderBy} ${orderType}


				order by ${orderByPoint} ${orderType}


				order by obj.addTime desc




			limit #{currentPage},#{pageSize}

